drop table best_hospitals;
create table best_hospitals as select h.id as id, h.name as hospital_name, h.state as state, avg(s.score)*count(s.score)/100 as weighted_score, sum(s.score) as aggregate_score, count(s.score) as count_score, avg(s.score) as average_score, stddev_pop(s.score) as variability_score  from scores_tbl s join hospitals_tbl h on (h.id=s.hospital_id) where s.score is not NULL and s.procedure_id not in ('ED_1b','ED_2b','OP_1','OP_18b','OP_20','OP_21','OP_3b','OP_5','SCIP_Inf_3') group by h.id, h.name, h.state order by weighted_score desc limit 10;